package com.jibestream.jibestreamandroidlibrary.styles;

import android.graphics.Color;
import android.graphics.Paint;

import com.jibestream.jibestreamandroidlibrary.utils.ColorsMaterialDesign;

/**
 * Created by emmanuel on 15-07-24.
 */
public class RenderStyle{
protected static final String TAG = "RenderStyle";
public Paint paintFill = null;
public Paint paintStroke = null;
public String name;
private Paint.Style style;

public RenderStyle() {
  this("", Paint.Style.FILL_AND_STROKE);

  final RenderStyle styleIdle = new RenderStyle(Paint.Style.FILL);
  styleIdle.paintFill.setColor(ColorsMaterialDesign.GREY6);
  set(styleIdle);
}

public RenderStyle(Paint.Style style) {
  this("", style);
}

public RenderStyle(String name, Paint.Style style) {
  this.name = name;
  this.style = style;
  calc();
}

private void calc() {
  if (style == null) return;
  paintFill = null;
  paintStroke = null;
  switch (style) {
    case FILL:
      paintFill = new Paint();
      paintFill.setStyle(Paint.Style.FILL);
      break;
    case STROKE:
      paintStroke = new Paint();
      paintStroke.setStyle(Paint.Style.STROKE);
      break;
    case FILL_AND_STROKE:
      paintFill = new Paint();
      paintFill.setStyle(Paint.Style.FILL);
      paintStroke = new Paint();
      paintStroke.setStyle(Paint.Style.STROKE);
      break;
  }
}

public Paint setFillColor(int color) {
  paintFill.setColor(color);
  return paintFill;
}

public Paint setFillAlpha(int alpha) {
  paintFill.setAlpha(alpha);
  return paintFill;
}


public Paint setStrokeColor(int color) {
  paintStroke.setColor(color);
  return paintStroke;
}

public Paint setStrokeAlpha(int alpha) {
  paintStroke.setAlpha(alpha);
  return paintStroke;
}

public Paint setStrokeWidth(float width) {
  paintStroke.setStrokeWidth(width);
  return paintStroke;
}

public void set(RenderStyle renderStyle) {
  name = renderStyle.name;
  paintFill = renderStyle.paintFill;
  paintStroke = renderStyle.paintStroke;
  style = renderStyle.getStyle();
}

public Paint.Style getStyle() {
  return style;
}

public void setStyle(Paint.Style style) {
  this.style = style;
  calc();
}
}
