package com.jibestream.jibestreamandroidlibrary.styles;


import android.graphics.Paint;

/**
 * Created by emmanuel on 2015-10-22.
 */
public class RenderStyleIcon {
public RenderStyle renderStyleBG;
public RenderStyle renderStyleMG;
public RenderStyle renderStyleFG;
public String name;

public RenderStyleIcon() {
  this("noName");
}

public RenderStyleIcon(String name) {
  this.name = name;
  renderStyleBG = new RenderStyle(name + "BG", Paint.Style.FILL);
  renderStyleBG.paintFill.setAntiAlias(true);
  renderStyleMG = new RenderStyle(name + "MG", Paint.Style.FILL);
  renderStyleMG.paintFill.setAntiAlias(true);
  renderStyleFG = new RenderStyle(name + "FG", Paint.Style.FILL);
  renderStyleFG.paintFill.setAntiAlias(true);
}

public void set(RenderStyleIcon renderStyleIcon) {
  renderStyleBG = renderStyleIcon.renderStyleBG;
  renderStyleMG = renderStyleIcon.renderStyleMG;
  renderStyleFG = renderStyleIcon.renderStyleFG;
}
}
