package com.jibestream.jibestreamandroidlibrary.styles.configDOs;

import com.google.gson.annotations.SerializedName;
import com.jibestream.jibestreamandroidlibrary.main.M;

/**
 * Data Object for for field exportStyles in web config file
 * that hides certain view types
 * Created by emmanuel on 2015-10-02.
 */
public class ExportStyles {

@SerializedName("Background")
public String BackgroundLayer;
@SerializedName("Parking-Lots")
public String ParkingLots;
@SerializedName("Mall-Boundary")
public String MallBoundary;
@SerializedName("Streets-SmallAlleys")
public String StreetsSmallAlleys;
@SerializedName("Streets-Minor")
public String StreetsMinor;
@SerializedName("Streets-Major")
public String StreetsMajor;
@SerializedName("Obstacles")
public String Obstacles;
@SerializedName("Units")
public String Units;
@SerializedName("Elevators")
public String Elevators;
@SerializedName("Escalators")
public String Escalators;

public static void setVisibility(ExportStyles exportStyles,M m) {
  if (exportStyles == null) return;
  if (exportStyles.BackgroundLayer.equalsIgnoreCase("hide")) m.setActiveBackground(false);
  if (exportStyles.ParkingLots.equalsIgnoreCase("hide")) m.setActiveParkingLots(false);
  if (exportStyles.MallBoundary.equalsIgnoreCase("hide")) m.setActiveMallBoundary(false);
  if (exportStyles.StreetsSmallAlleys.equalsIgnoreCase("hide")) m.setActiveStreetsSmallAlleys(false);
  if (exportStyles.StreetsMinor.equalsIgnoreCase("hide")) m.setActiveStreetsMinor(false);
  if (exportStyles.StreetsMajor.equalsIgnoreCase("hide")) m.setActiveStreetsMajor(false);
  if (exportStyles.Obstacles.equalsIgnoreCase("hide")) m.setActiveObstacles(false);
  if (exportStyles.Units.equalsIgnoreCase("hide")) m.setActiveUnits(false);
  if (exportStyles.Elevators.equalsIgnoreCase("hide")) m.setActiveElevators(false);
  if (exportStyles.Escalators.equalsIgnoreCase("hide")) m.setActiveEscalators(false);
}
}
