package com.jibestream.jibestreamandroidlibrary.utils;

/**
 * Utility Object that stores a pivot point in ratios
 * and provides placeholders for offsets
 * Created by emmanuel on 15-07-02.
 */
public class Ninegrid {
public final static int TopLeft = 1;
public final static int TopMiddle = 2;
public final static int TopRight = 3;
public final static int MiddleLeft = 4;
public final static int MiddleMiddle = 5;
public final static int MiddleRight = 6;
public final static int BottomLeft = 7;
public final static int BottomMiddle = 8;
public final static int BottomRight = 9;
public float x;
public float y;
public float offsetX;
public float offsetY;

/**
 * Create a ningrid instance in terms of custom ratios
 * @param x
 * @param y
 */
public Ninegrid(float x, float y) {
  this.x = x;
  this.y = y;
}

/**
 * Create a ninfegrid from one of the available static fields
 * @param ng
 */
public Ninegrid(int ng) {
  switch (ng) {
    case Ninegrid.TopLeft:
      x = 0f;
      y = 0f;
      break;
    case Ninegrid.TopMiddle:
      x = 0.5f;
      y = 0f;
      break;
    case Ninegrid.TopRight:
      x = 1f;
      y = 0f;
      break;
    case Ninegrid.MiddleLeft:
      x = 0f;
      y = 0.5f;
      break;
    case Ninegrid.MiddleMiddle:
      x = 0.5f;
      y = 0.5f;
      break;
    case Ninegrid.MiddleRight:
      x = 1f;
      y = 0.5f;
      break;
    case Ninegrid.BottomLeft:
      x = 0f;
      y = 1f;
      break;
    case Ninegrid.BottomMiddle:
      x = 0.5f;
      y = 1f;
      break;
    case Ninegrid.BottomRight:
      x = 1f;
      y = 1f;
      break;
  }
}

}